package org.cloudfoundry.autoscaler.scheduler.filter;

import static org.assertj.core.api.AssertionsForClassTypes.assertThat;

import jakarta.servlet.FilterChain;
import org.cloudfoundry.autoscaler.scheduler.conf.CfServerConfiguration;
import org.junit.Before;
import org.junit.Test;
import org.mockito.Mockito;
import org.springframework.mock.web.MockFilterChain;
import org.springframework.mock.web.MockHttpServletRequest;
import org.springframework.mock.web.MockHttpServletResponse;

public class HttpAuthFilterTest {

  private MockHttpServletRequest request;
  private MockHttpServletResponse response;

  private FilterChain filterChain;
  private HttpAuthFilter httpAuthFilter;

  private CfServerConfiguration cfServerConfiguration;

  @Before
  public void setup() {
    request = new MockHttpServletRequest();
    response = new MockHttpServletResponse();
    filterChain = new MockFilterChain();

    cfServerConfiguration = Mockito.mock(CfServerConfiguration.class);
    CfServerConfiguration.HealthServer healthServerConfig =
        Mockito.mock(CfServerConfiguration.HealthServer.class);

    Mockito.when(healthServerConfig.getUsername()).thenReturn("test-user");
    Mockito.when(healthServerConfig.getPassword()).thenReturn("test-password");

    Mockito.when(cfServerConfiguration.getHealthserver()).thenReturn(healthServerConfig);

    httpAuthFilter = new HttpAuthFilter(cfServerConfiguration);
  }

  @Test
  public void testDoFilterWithMissingXfccHeaderGetsSkipped() throws Exception {

    httpAuthFilter.doFilterInternal(request, response, filterChain);
    assertThat(response.getStatus()).isEqualTo(200);
  }

  @Test
  public void testDoFilterWithXfccHeaderWithInvalidOrgUnitReturnsUnauthorized() throws Exception {

    String certValue =
        "";

    request.addHeader("X-Forwarded-Client-Cert", certValue);

    httpAuthFilter.doFilterInternal(request, response, filterChain);

    assertThat(response.getStatus()).isEqualTo(403);
    assertThat(response.getErrorMessage()).isEqualTo("Unauthorized OrganizationalUnit");
  }

  @Test
  public void testDoFilterWithXfccHeaderReturnSuccess() throws Exception {
    String certValue =
        "";

    Mockito.when(cfServerConfiguration.getValidOrgGuid()).thenReturn("validorg");
    Mockito.when(cfServerConfiguration.getValidSpaceGuid()).thenReturn("validspace");

    request.addHeader("X-Forwarded-Client-Cert", certValue);

    httpAuthFilter.doFilterInternal(request, response, filterChain);

    assertThat(response.getStatus()).isEqualTo(200);
  }

  @Test
  public void testDoFilterWithInvalidCertificateThrowsException() throws Exception {
    String certValue =
        "";
    request.addHeader("X-Forwarded-Client-Cert", certValue);

    cfServerConfiguration.setValidSpaceGuid("some-org-guid");
    cfServerConfiguration.setValidSpaceGuid("some-space-guid");
    httpAuthFilter.doFilterInternal(request, response, filterChain);

    assertThat(response.getStatus()).isEqualTo(400);
    assertThat(response.getErrorMessage())
        .contains("Invalid certificate: Could not parse certificate");
  }

  @Test
  public void testDoFilterHttpHealthReturnsSuccess() throws Exception {
    var username = "test-user";
    var password = "test-password";

    this.request.setScheme("http");
    this.request.setRequestURI("/health");
    this.request.setMethod("GET");
    this.request.addHeader(
        "Authorization",
        "Basic "
            + java.util.Base64.getEncoder().encodeToString((username + ":" + password).getBytes()));

    httpAuthFilter.doFilterInternal(request, response, filterChain);
    assertThat(response.getStatus()).isEqualTo(200);
  }

  @Test
  public void testDoFilterHttpHealthReturnsErrorWithWrongCredentials() throws Exception {
    var username = "incorrect-username";
    var password = "incorrect-password";

    this.request.setScheme("http");
    this.request.setRequestURI("/health");
    this.request.setMethod("GET");
    this.request.addHeader(
        "Authorization",
        "Basic "
            + java.util.Base64.getEncoder().encodeToString((username + ":" + password).getBytes()));

    httpAuthFilter.doFilterInternal(request, response, filterChain);
    assertThat(response.getStatus()).isEqualTo(401);
    assertThat(response.getErrorMessage()).isEqualTo("Unauthorized");
  }
}
